$.ajax({url:"http://newsapi.org/v2/top-headlines?country=us&apiKey=0e47546fdc5a4e1bbc29a0ab34251404",type:"get",success:function(e){for(i=0;i<=5&&i!=e.articles.length;i++)$("#left-aside").append(`<div id="news-container">    \n                            <img src="${e.articles[i].urlToImage}" alt="image">\n                            <a href="${e.articles[i].url}"><h3>${e.articles[i].title}</h3></a>\n                            <a href="${e.articles[i].url}"><p id="description">${e.articles[i].description}</p></a>\n                    </div>`)},error:function(i){console.log(i)}});